<?phpxenon_custom_link('searchxenonpost','xenon-addons/search-post/search-post.php');function sp_admin() {      option_menu('search-post-admin.php', 'search-post-admin', 'addonpage','3','Search Post');  }xenon_core('xenon_add_menu', 'sp_admin');//setup finishfunction search_post_xenon() {if(isset($_GET['url']) && $_GET['url']!="" && user_name()!='') {$bwcat1=@defence_sql_injection($_GET['url']);$f1=@defence_sql_injection($_GET['f']);$type=@defence_sql_injection($_GET['type']);$user_name2234=user_name();					if ( user_roll() == "administrator" || user_roll() == "moderator" ) {if($type=="01") {$result = @mysql_query("SELECT * FROM frndzk_post WHERE post LIKE'%$bwcat1%' ORDER by id desc LIMIT $f1,50");}else {$result = @mysql_query("SELECT * FROM frndzk_post WHERE tags LIKE'%$bwcat1%' ORDER by id desc LIMIT $f1,50");}					}					else {					if($type=="01") {$result = @mysql_query("SELECT * FROM frndzk_post WHERE post LIKE'%$bwcat1%' AND postedby='$user_name2234' ORDER by id desc LIMIT $f1,50");}else {$result = @mysql_query("SELECT * FROM frndzk_post WHERE tags LIKE'%$bwcat1%' AND postedby='$user_name2234' ORDER by id desc LIMIT $f1,50");}}if ( @mysql_num_rows($result) > 0 ) {echo'<table class="listing" cellpadding="0" cellspacing="0">					<tr>						<th class="first" width="177">Page Name</th>						<th>Posted By</th>						<th>Enable Comment</th>						<th>Date</th>						<th>Edit</th>						<th class="last">Delete</th>					</tr>';while($pages = @mysql_fetch_array($result)) {					echo'<tr>						<td class="first style1"><a target="_blank" href="'.home_url().'/'.xenon_link_post().$pages['shortname'].'">' . $pages['post'] . '</a></td>						<td>' . $pages['postedby'] . '</td>						<td>' . $pages['comstts'] . '</td>						<td>' . $pages['date'] . '<br>' . $pages['time'] . '</td>';												echo'						<td><a href="post.php?action=edit&number=edit&id=' . $pages['id'] . '"><img src="img/save-icon.gif" width="16" height="16" alt="" /></a></td>						<td><a href="post.php?action=delete&number=single&id=' . $pages['id'] . '"><img src="img/hr.gif" width="16" height="16" alt="" /></a></td>						</tr>'; 						}				}				echo'</table>+--+';									}else {echo'no access';}}?>